// UserForm.js
import React from 'react';
import { connect } from 'react-redux';

class UserForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lastName: '',
    };
  }

  handleNameChange = (e) => {
    this.props.updateName(e.target.value);
  };

  handleLastNameChange = (e) => {
    this.setState({ lastName: e.target.value });
  };

  handleAgeChange = (e) => {
    this.props.updateAge(e.target.value);
  };

  handleSave = () => {
    const userData = {
      name: this.props.name,
      lastName: this.state.lastName,
      age: this.props.age,
    };

    // Додаємо вашу логіку для збереження даних, наприклад, в Redux store або виклик API
    // Наразі просто виведемо дані у консоль
    console.log('Saved User Data:', userData);

    // Очищаємо поля після збереження
    this.props.updateName('');
    this.setState({ lastName: '' });
    this.props.updateAge('');
  };

  render() {
    return (
      <div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={this.props.name}
            onChange={this.handleNameChange}
          />
        </div>
        <div>
          <label>Last Name:</label>
          <input
            type="text"
            value={this.state.lastName}
            onChange={this.handleLastNameChange}
          />
        </div>
        <div>
          <label>Age:</label>
          <input
            type="text"
            value={this.props.age}
            onChange={this.handleAgeChange}
          />
        </div>
        <div>
          <button onClick={this.handleSave}>Save</button>
        </div>
        {/* Окремий div для відображення збережених даних */}
        <div>
          <h2>Saved User Details</h2>
          <p>Name: {this.props.name}</p>
          <p>Last Name: {this.state.lastName}</p>
          <p>Age: {this.props.age}</p>
        </div>
      </div>
    );
  }
}

// Підключення компонента до Redux store
const mapStateToProps = (state) => ({
  name: state.name,
  age: state.age,
});

const mapDispatchToProps = (dispatch) => ({
  updateName: (name) => dispatch({ type: 'UPDATE_NAME', payload: name }),
  updateAge: (age) => dispatch({ type: 'UPDATE_AGE', payload: age }),
});

export default connect(mapStateToProps, mapDispatchToProps)(UserForm);
