import React from 'react';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import UserForm from './UserForm';

// Створіть редюсер для обробки дій користувача
const initialState = {
    name: '',
    lastName: '',
    age: '',
    savedUserData: [],
  };
  
  const userReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'UPDATE_NAME':
        return { ...state, name: action.payload };
      case 'UPDATE_LAST_NAME':
        return { ...state, lastName: action.payload };
      case 'UPDATE_AGE':
        return { ...state, age: action.payload };
      case 'SAVE_USER_DATA':
        return {
          ...state,
          savedUserData: [...state.savedUserData, action.payload],
          name: '',
          lastName: '',
          age: '',
        };
      default:
        return state;
    }
  };
  

// Створіть Redux store з редюсером
const store = createStore(userReducer);

// Огортаємо кореневий компонент в Provider, щоб передати store усім дочірнім компонентам
const App = () => {
  return (
    <Provider store={store}>
      <div>
        <h1>Redux User Form</h1>
        <UserForm />
      </div>
    </Provider>
  );
};

export default App;
