// UserForm.js
import React from 'react';
import { connect } from 'react-redux';

class UserForm extends React.Component {
  handleNameChange = (e) => {
    this.props.updateName(e.target.value);
  };

  handleLastNameChange = (e) => {
    this.props.updateLastName(e.target.value);
  };

  handleAgeChange = (e) => {
    this.props.updateAge(e.target.value);
  };

  handleSave = () => {
    const userData = {
      name: this.props.name,
      lastName: this.props.lastName,
      age: this.props.age,
    };

    // Додаємо дію для збереження даних в Redux store
    this.props.saveUserData(userData);
  };

  render() {
    return (
      <div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={this.props.name}
            onChange={this.handleNameChange}
          />
        </div>
        <div>
          <label>Last Name:</label>
          <input
            type="text"
            value={this.props.lastName}
            onChange={this.handleLastNameChange}
          />
        </div>
        <div>
          <label>Age:</label>
          <input
            type="text"
            value={this.props.age}
            onChange={this.handleAgeChange}
          />
        </div>
        <div>
          <button onClick={this.handleSave}>Save</button>
        </div>
        {/* Окремий div для відображення збережених даних */}
        <div>
          <h2>Saved User Details</h2>
          {this.props.savedUserData.map((user, index) => (
            <div key={index}>
              <p>Name: {user.name}</p>
              <p>Last Name: {user.lastName}</p>
              <p>Age: {user.age}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }
}

// Підключення компонента до Redux store
const mapStateToProps = (state) => ({
  name: state.name,
  lastName: state.lastName,
  age: state.age,
  savedUserData: state.savedUserData,
});

const mapDispatchToProps = (dispatch) => ({
  updateName: (name) => dispatch({ type: 'UPDATE_NAME', payload: name }),
  updateLastName: (lastName) =>
    dispatch({ type: 'UPDATE_LAST_NAME', payload: lastName }),
  updateAge: (age) => dispatch({ type: 'UPDATE_AGE', payload: age }),
  saveUserData: (userData) =>
    dispatch({ type: 'SAVE_USER_DATA', payload: userData }),
});

export default connect(mapStateToProps, mapDispatchToProps)(UserForm);
