import React from 'react';
import { connect } from 'react-redux';

const UserForm = ({
    name,
    lastName,
    age,
    savedUserData,
    updateName,
    updateLastName,
    updateAge,
    saveUserData,
}) => {
const handleNameChange = (e) => {
  updateName(e.target.value);
};

const handleLastNameChange = (e) => {
  updateLastName(e.target.value);
};

const handleAgeChange = (e) => {
  updateAge(e.target.value);
};

const handleSave = () => {
  const userData = {
    name,
    lastName,
    age,
  };

  saveUserData(userData);
};

    return (
      <div>
        <div>
          <label>Name:</label>
          <input
            type="text"
            value={name}
            onChange={handleNameChange}
          />
        </div>
        <div>
          <label>Last Name:</label>
          <input
            type="text"
            value={lastName}
            onChange={handleLastNameChange}
          />
        </div>
        <div>
          <label>Age:</label>
          <input
            type="text"
            value={age}
            onChange={handleAgeChange}
          />
        </div>
        <div>
          <button onClick={handleSave}>Save</button>
        </div>
        {/* Окремий div для відображення збережених даних */}
        <div>
          <h2>Saved User Details</h2>
          {savedUserData.map((user, index) => (
            <div key={index}>
              <p>Name: {user.name}</p>
              <p>Last Name: {user.lastName}</p>
              <p>Age: {user.age}</p>
            </div>
          ))}
        </div>
      </div>
    );
};
const mapStateToProps = (state) => ({
    ...state.user,
  });
  
  const mapDispatchToProps = (dispatch) => ({
    updateName: (name) => dispatch({ type: 'UPDATE_NAME', payload: name }),
    updateLastName: (lastName) =>
      dispatch({ type: 'UPDATE_LAST_NAME', payload: lastName }),
    updateAge: (age) => dispatch({ type: 'UPDATE_AGE', payload: age }),
    saveUserData: (userData) =>
      dispatch({ type: 'SAVE_USER_DATA', payload: userData }),
  });
  
  export default connect(mapStateToProps, mapDispatchToProps)(UserForm);
